mapper.update:

UPDATE board
SET readCount = readCount + 1
WHERE seq = #{seq}

mapper.select:

SELECT seq, subject, content, writer, reg_date, readCount
FROM board
WHERE seq = #{seq}

mapper.update:

UPDATE board
SET subject = #{subject, jdbcType=VARCHAR}, content = #{content, jdbcType=VARCHAR}
WHERE seq = #{seq, jdbcType=VARCHAR}
